package Train240309;

public class A6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int a = 50;
	int b = 50;
	
	System.out.println("a와 b가 같다 " + (a==b));
	System.out.println("a가 b보다 클때 " + (a>b));
	System.out.println("a가 b보다 작을때 " + (a<b));
	System.out.println("a가 b와 같으면서, a가 30보다 클때 "
	 + ((a==b) && (a>30)));
	//&& 가 and
	System.out.println("a는 이 아닐때 " + !(a == 50));
	// !가 부정.
	

	
	
	
	}

}
